/**
 * api.ts
 * Why: Centralize HTTP client config for consistent API calls.
 * Purpose: Creates an axios instance with base URL and credentials for cookie-based JWT.
 */

 import axios from "axios";

 const api = axios.create({
   baseURL: "http://localhost:5000/api",
   withCredentials: true, // send/receive JWT cookies
 });
 
 export default api;
