import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import Navbar from "../components/Navbar";

// Types
export type Question = {
  id: number;
  title: string;
  description?: string;
  type: "text" | "number"; // If your API supports boolean, prefer that. We infer Yes/No from title/description for now.
  category: "demographic" | "health" | "financial";
};

export type GroupedQuestions = {
  demographic: Question[];
  health: Question[];
  financial: Question[];
};

const tabs: Array<keyof GroupedQuestions> = ["demographic", "health", "financial"];

export default function Survey() {
  const [questions, setQuestions] = useState<GroupedQuestions | null>(null);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState<keyof GroupedQuestions>("demographic");
  const navigate = useNavigate();

  // Fetch & dedupe
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const res = await api.get("/questions");
        const raw = res.data as GroupedQuestions;

        const deduped: GroupedQuestions = { demographic: [], health: [], financial: [] };
        const seen = new Set<number>();
        for (const cat of tabs) {
          for (const q of raw[cat] || []) {
            if (!seen.has(q.id)) {
              deduped[cat].push(q);
              seen.add(q.id);
            }
          }
        }
        setQuestions(deduped);
      } catch (err) {
        console.error(err);
        setError("Failed to load survey questions.");
      }
    };
    fetchQuestions();
  }, []);

  // Helpers
  const handleChange = (questionId: number, value: string) => {
    setAnswers((prev) => ({ ...prev, [questionId]: value }));
  };

  const isYesNo = (q: Question) => {
    const t = (q.title || "").toLowerCase();
    const d = (q.description || "").toLowerCase();
    // Heuristics: treat as Yes/No if explicitly mentions yes/no or is phrased as a closed question.
    return (
      t.includes("yes/no") ||
      d.includes("yes/no") ||
      /^do\s|^did\s|^have\s|^has\s|^are\s|^is\s|^was\s|^were\s/.test(t)
    );
  };

  const renderAnswerCell = (q: Question) => {
    if (isYesNo(q)) {
      return (
        <div className="flex items-center gap-6">
          <label className="inline-flex items-center gap-2">
            <input
              type="radio"
              name={`q-${q.id}`}
              value="Yes"
              checked={answers[q.id] === "Yes"}
              onChange={() => handleChange(q.id, "Yes")}
              required
            />
            <span>Yes</span>
          </label>
          <label className="inline-flex items-center gap-2">
            <input
              type="radio"
              name={`q-${q.id}`}
              value="No"
              checked={answers[q.id] === "No"}
              onChange={() => handleChange(q.id, "No")}
              required
            />
            <span>No</span>
          </label>
        </div>
      );
    }

    return (
      <input
        type={q.type}
        className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-1 focus:ring-gray-400"
        value={answers[q.id] || ""}
        onChange={(e) => handleChange(q.id, e.target.value)}
        required
        placeholder={q.type === "number" ? "Enter number" : "Type your answer"}
      />
    );
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const payload = Object.entries(answers).map(([questionId, answer]) => ({
      questionId: Number(questionId),
      answer,
    }));

    try {
      await api.post("/responses", { responses: payload });
      navigate("/responses");
    } catch (err: any) {
      console.error(err);
      setError(err?.response?.data?.error || "Failed to submit survey.");
    }
  };

  if (!questions) return <div className="p-4">Loading...</div>;

  return (
    <>
      <Navbar />

      <div className="max-w-4xl mx-auto p-4">
        {/* Title */}
        <h1 className="text-2xl font-semibold mb-4">Survey</h1>

        {/* Error */}
        {error && (
          <div className="mb-4 text-sm text-red-600 border border-red-200 bg-red-50 rounded p-3">{error}</div>
        )}

        {/* Tabs (simple step indicator) */}
        <div className="flex items-center gap-2 mb-6">
          {tabs.map((tab) => (
            <button
              key={tab}
              type="button"
              onClick={() => setActiveTab(tab)}
              className={`px-3 py-1 rounded-full border text-sm capitalize transition-colors ${
                activeTab === tab ? "bg-gray-900 text-white border-gray-900" : "bg-white text-gray-700 border-gray-300 hover:bg-gray-50"
              }`}
            >
              {tab}
            </button>
          ))}
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Two-column, table-like layout */}
          <div className="rounded-xl border border-gray-200 overflow-hidden">
            {/* Header row */}
            <div className="grid grid-cols-2 bg-gray-50 text-sm font-medium text-gray-700 px-4 py-2">
              <div>Question</div>
              <div>Answer</div>
            </div>

            {/* Rows */}
            <div>
              {questions[activeTab].map((q, idx) => (
                <div
                  key={q.id}
                  className={`grid grid-cols-2 items-center px-4 py-3 text-sm ${idx % 2 === 0 ? "bg-white" : "bg-gray-50"}`}
                >
                  {/* Left: question text */}
                  <div className="pr-4">
                    <div className="font-medium text-gray-900">{q.title} <span className="text-red-500">*</span></div>
                    {q.description && <div className="text-gray-500 mt-0.5">{q.description}</div>}
                  </div>

                  {/* Right: input */}
                  <div className="pl-4">{renderAnswerCell(q)}</div>
                </div>
              ))}
            </div>
          </div>

          {/* Navigation */}
          <div className="flex items-center justify-between">
            {activeTab !== "demographic" ? (
              <button
                type="button"
                onClick={() => setActiveTab(tabs[tabs.indexOf(activeTab) - 1])}
                className="px-4 py-2 rounded border border-gray-300 text-gray-700 hover:bg-gray-50"
              >
                Previous
              </button>
            ) : (
              <div />
            )}

            {activeTab !== "financial" ? (
              <button
                type="button"
                onClick={() => {
                  const allFilled = questions[activeTab].every((q) => (answers[q.id] || "").toString().trim());
                  if (!allFilled) {
                    alert("Please answer all questions in this section.");
                    return;
                  }
                  setActiveTab(tabs[tabs.indexOf(activeTab) + 1]);
                }}
                className="px-4 py-2 rounded bg-gray-900 text-white hover:opacity-90"
              >
                Next
              </button>
            ) : (
              <button type="submit" className="px-5 py-2 rounded bg-gray-900 text-white hover:opacity-90">
                Submit
              </button>
            )}
          </div>
        </form>
      </div>
    </>
  );
}
