import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import Navbar from "../components/Navbar";


type Question = {
  id: number;
  title: string;
  description?: string;
  type: "text" | "number";
  category: "demographic" | "health" | "financial";
};

type GroupedQuestions = {
  demographic: Question[];
  health: Question[];
  financial: Question[];
};

export default function Survey() {
  const [questions, setQuestions] = useState<GroupedQuestions | null>(null);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Fetch questions on load
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const res = await api.get("/questions");
        setQuestions(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load survey questions");
      }
    };
    fetchQuestions();
  }, []);

  // Handle input change
  const handleChange = (questionId: number, value: string) => {
    setAnswers((prev) => ({
      ...prev,
      [questionId]: value,
    }));
  };

  // Submit responses
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const payload = Object.entries(answers).map(([questionId, answer]) => ({
      questionId: Number(questionId),
      answer,
    }));

    try {
      await api.post("/responses", { responses: payload });
      navigate("/responses");
    } catch (err: any) {
      console.error(err);
      setError(err?.response?.data?.error || "Failed to submit survey");
    }
  };

  if (!questions) return <div className="p-4">Loading...</div>;

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Survey Form</h1>

      {error && <div className="text-red-600 mb-4">{error}</div>}

      <form onSubmit={handleSubmit} className="space-y-8">
        {Object.entries(questions).map(([category, questions]) => (
          <div key={category}>
            <h2 className="text-xl font-semibold capitalize mb-2">{category}</h2>
            <div className="space-y-4">
              {questions.map((q) => (
                <div key={q.id}>
                  <label className="block font-medium mb-1">{q.title}</label>
                  {q.description && (
                    <p className="text-sm text-gray-500 mb-1">{q.description}</p>
                  )}
                  <input
                    type={q.type}
                    className="w-full border p-2 rounded"
                    value={answers[q.id] || ""}
                    onChange={(e) => handleChange(q.id, e.target.value)}
                    required
                  />
                </div>
              ))}
            </div>
          </div>
        ))}

        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Submit Survey
        </button>
      </form>
    </div>
  );
}
