/**
 * App.tsx
 * -----------------------
 * Why: This is the root component of the React application.
 * Purpose: Sets up client-side routing for all main pages using React Router.
 */

 import React from 'react';
 import './App.css';
 import { BrowserRouter, Routes, Route } from "react-router-dom";
 import Login from "./pages/Login";
 import Register from "./pages/Register";
 import Survey from "./pages/Survey";
 import Responses from "./pages/Responses";

 function App() {  
   return (
     <div className="App">
       {/* 
         Why: Define navigation paths for the application.
         Purpose: Map URL routes to their corresponding components.
       */}
       <BrowserRouter>
         <Routes>
           {/* Why: Allow users to log in. 
               Purpose: Route /login to Login page. */}
          <Route path="/" element={<Login />} />

           <Route path="/login" element={<Login />} />

           {/* Why: Allow new users to sign up.
               Purpose: Route /register to Register page. */}
           <Route path="/register" element={<Register />} />

           {/* Why: Display the main survey form.
               Purpose: Route /survey to Survey page. */}
           <Route path="/survey" element={<Survey />} />

           {/* Why: Let users view their past survey submissions.
               Purpose: Route /responses to Responses page. */}
           <Route path="/responses" element={<Responses />} />
         </Routes>
       </BrowserRouter>
     </div>
   );
 }

 export default App;
