import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";
import Navbar from "../components/Navbar";
import {
  Tabs,
  TabsList,
  TabsTrigger,
  TabsContent,
} from "@/components/ui/tabs";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectTrigger, SelectValue, SelectContent, SelectItem } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";

type Question = {
  id: number;
  title: string;
  description?: string;
  type: "text" | "number";
  category: "demographic" | "health" | "financial";
};

type GroupedQuestions = {
  demographic: Question[];
  health: Question[];
  financial: Question[];
};

export default function Survey() {
  const [questions, setQuestions] = useState<GroupedQuestions | null>(null);
  const [answers, setAnswers] = useState<Record<number, string>>({});
  const [error, setError] = useState("");
  const [tab, setTab] = useState<keyof GroupedQuestions>("demographic");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const res = await api.get("/questions");
        setQuestions(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load survey questions.");
      }
    };
    fetchQuestions();
  }, []);

  const handleChange = (id: number, value: string) => {
    setAnswers((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const payload = Object.entries(answers).map(([questionId, answer]) => ({
      questionId: Number(questionId),
      answer,
    }));

    try {
      await api.post("/responses", { responses: payload });
      navigate("/responses");
    } catch (err: any) {
      console.error(err);
      setError(err?.response?.data?.error || "Failed to submit survey.");
    }
  };

  const renderInput = (q: Question) => {
    if (q.title.toLowerCase().includes("sex")) {
      return (
        <RadioGroup
          defaultValue={answers[q.id] || ""}
          onValueChange={(val) => handleChange(q.id, val)}
        >
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="Male" id={`male-${q.id}`} />
            <Label htmlFor={`male-${q.id}`}>Male</Label>
          </div>
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="Female" id={`female-${q.id}`} />
            <Label htmlFor={`female-${q.id}`}>Female</Label>
          </div>
        </RadioGroup>
      );
    }

    if (q.title.toLowerCase().includes("height")) {
      return (
        <div className="flex gap-2">
          <Select onValueChange={(val) => handleChange(q.id, `${val}'${answers[q.id]?.split("'")[1] || "0"}`)}>
            <SelectTrigger className="w-24">
              <SelectValue placeholder="Feet" />
            </SelectTrigger>
            <SelectContent>
              {["4", "5", "6", "7"].map((ft) => (
                <SelectItem key={ft} value={ft}>
                  {ft} ft
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select onValueChange={(val) => handleChange(q.id, `${answers[q.id]?.split("'")[0] || "5"}'${val}`)}>
            <SelectTrigger className="w-24">
              <SelectValue placeholder="Inches" />
            </SelectTrigger>
            <SelectContent>
              {Array.from({ length: 12 }, (_, i) => i).map((inches) => (
                <SelectItem key={inches} value={inches.toString()}>
                  {inches} in
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      );
    }

    return (
      <Input
        type={q.type}
        placeholder={q.title.includes("age") ? "e.g. 35" : ""}
        value={answers[q.id] || ""}
        onChange={(e) => handleChange(q.id, e.target.value)}
        required
      />
    );
  };

  if (!questions) return <div className="p-4">Loading...</div>;

  return (
    <>
      <Navbar />
      <div className="max-w-3xl mx-auto p-4">
        <h1 className="text-3xl font-bold mb-6">Survey Form</h1>

        <Tabs defaultValue="demographic" value={tab} onValueChange={(v) => setTab(v as any)} className="w-full">
          <TabsList className="grid w-full grid-cols-3 mb-6">
            <TabsTrigger value="demographic">Demographic</TabsTrigger>
            <TabsTrigger value="health">Health</TabsTrigger>
            <TabsTrigger value="financial">Financial</TabsTrigger>
          </TabsList>

          {Object.entries(questions).map(([category, group]) => (
            <TabsContent key={category} value={category}>
              <form onSubmit={handleSubmit} className="space-y-6">
                {group.map((q) => (
                  <Card key={q.id} className="p-4">
                    <CardContent className="space-y-2 p-0">
                      <Label>
                        {q.title}
                        {q.description && (
                          <span className="text-sm text-gray-500 ml-2">({q.description})</span>
                        )}
                        <span className="text-red-500 ml-1">*</span>
                      </Label>
                      {renderInput(q)}
                    </CardContent>
                  </Card>
                ))}

                {category === "financial" && (
                  <Button type="submit" className="mt-4">
                    Submit Survey
                  </Button>
                )}
              </form>
            </TabsContent>
          ))}
        </Tabs>
      </div>
    </>
  );
}
