import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../services/api";

// --- Types ---
type Category = string;

type Question = {
  id: number;
  title: string;
  description?: string;
  type: "text" | "number" | "mcq";
  category: Category;
  required?: boolean;
  options?: string[]; // for mcq
};

type AnswerMap = Record<number, string>;

// --- Helpers ---
const DRAFT_KEY = "survey_draft_simple_v1";
const cx = (...xs: Array<string | false | undefined>) => xs.filter(Boolean).join(" ");

export default function Survey() {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState<Question[]>([]);
  const [answers, setAnswers] = useState<AnswerMap>(() => {
    try {
      return JSON.parse(localStorage.getItem(DRAFT_KEY) || "{}");
    } catch {
      return {};
    }
  });
  const [activeCat, setActiveCat] = useState<Category>("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    (async () => {
      try {
        const res = await api.get<Question[]>("/questions");
        const qs = res.data || [];
        setQuestions(qs);
        if (qs[0]?.category) setActiveCat(qs[0].category);
      } catch (e: any) {
        console.error(e);
        setError(e?.response?.data?.error || "Failed to load questions.");
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  useEffect(() => {
    localStorage.setItem(DRAFT_KEY, JSON.stringify(answers));
  }, [answers]);

  const categories = useMemo(() => Array.from(new Set(questions.map((q) => q.category))), [questions]);

  const grouped = useMemo(() => {
    const m = new Map<Category, Question[]>();
    categories.forEach((c) => m.set(c, []));
    questions.forEach((q) => {
      const arr = m.get(q.category) || [];
      arr.push(q);
      m.set(q.category, arr);
    });
    return m;
  }, [questions, categories]);

  const requiredOk = useMemo(() => {
    return questions.every((q) => {
      if (q.required === false) return true;
      return (answers[q.id] ?? "").toString().trim().length > 0;
    });
  }, [questions, answers]);

  const total = questions.length;
  const done = questions.filter((q) => (answers[q.id] ?? "").toString().trim()).length;
  const progress = total ? Math.round((done / total) * 100) : 0;

  function setAnswer(q: Question, v: string) {
    setAnswers((prev) => ({ ...prev, [q.id]: v }));
  }

  async function handleSubmit() {
    if (!done) {
      setError("Please answer at least one question.");
      return;
    }
    setSubmitting(true);
    setError("");

    const payload = Object.entries(answers)
      .filter(([, v]) => String(v).trim().length > 0)
      .map(([questionId, answer]) => ({ questionId: Number(questionId), answer: String(answer) }));

    try {
      try {
        await api.put("/responses", { responses: payload });
      } catch {
        await api.post("/responses", { responses: payload });
      }
      localStorage.removeItem(DRAFT_KEY);
      navigate("/responses");
    } catch (e: any) {
      console.error(e);
      setError(e?.response?.data?.error || "Failed to submit.");
    } finally {
      setSubmitting(false);
    }
  }

  if (loading) return <div className="p-6">Loading…</div>;
  if (error) return <div className="p-6 text-red-600">{error}</div>;

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <header className="border-b">
        <div className="max-w-3xl mx-auto px-4 py-4">
          <h1 className="text-xl font-semibold">Quick Survey</h1>
          <div className="mt-2 flex items-center gap-3">
            <div className="h-2 flex-1 bg-gray-200 rounded-full overflow-hidden">
              <div className="h-full bg-black" style={{ width: `${progress}%` }} />
            </div>
            <span className="text-sm text-gray-600 w-14 text-right">{progress}%</span>
          </div>
        </div>
      </header>

      <main className="max-w-3xl mx-auto px-4 py-4">
        {/* Simple Tabs */}
        <div className="flex gap-2 overflow-x-auto pb-2">
          {categories.map((cat) => (
            <button
              key={String(cat)}
              onClick={() => setActiveCat(cat)}
              className={cx(
                "px-3 py-1.5 rounded-full border text-sm",
                activeCat === cat ? "bg-black text-white border-black" : "bg-white border-gray-300"
              )}
            >
              <span className="capitalize">{String(cat)}</span>
            </button>
          ))}
        </div>

        {/* Questions (single column) */}
        <div className="mt-3 space-y-4">
          {(grouped.get(activeCat) || []).map((q) => (
            <div key={q.id} className="border rounded-xl p-4">
              <label className="block font-medium">
                {q.title} {q.required !== false && <span className="text-red-500">*</span>}
              </label>
              {q.description && (
                <p className="text-sm text-gray-500 mt-0.5">{q.description}</p>
              )}

              <div className="mt-2">
                {q.type === "text" && (
                  <input
                    type="text"
                    className="w-full border rounded-lg px-3 py-2"
                    placeholder="Type your answer"
                    value={answers[q.id] ?? ""}
                    onChange={(e) => setAnswer(q, e.target.value)}
                  />
                )}

                {q.type === "number" && (
                  <input
                    type="number"
                    className="w-full border rounded-lg px-3 py-2"
                    placeholder="Enter a number"
                    value={answers[q.id] ?? ""}
                    onChange={(e) => setAnswer(q, e.target.value)}
                  />
                )}

                {q.type === "mcq" && (
                  <div className="flex flex-col gap-2">
                    {(q.options || []).length === 0 ? (
                      <span className="text-sm text-gray-500">No options provided.</span>
                    ) : (
                      q.options!.map((opt, i) => (
                        <label key={i} className="inline-flex items-center gap-2">
                          <input
                            type="radio"
                            name={`q-${q.id}`}
                            checked={answers[q.id] === opt}
                            onChange={() => setAnswer(q, opt)}
                          />
                          <span>{opt}</span>
                        </label>
                      ))
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}

          {(grouped.get(activeCat) || []).length === 0 && (
            <div className="text-gray-600">No questions here.</div>
          )}
        </div>

        {/* Actions */}
        <div className="mt-6 flex flex-col sm:flex-row gap-2 sm:items-center sm:justify-between">
          <button
            onClick={() => {
              localStorage.removeItem(DRAFT_KEY);
              setAnswers({});
            }}
            className="px-4 py-2 border rounded-lg hover:bg-gray-50"
          >
            Clear
          </button>
          <button
            onClick={handleSubmit}
            disabled={submitting || !requiredOk}
            className={cx(
              "px-5 py-2 rounded-lg text-white",
              submitting || !requiredOk ? "bg-gray-400 cursor-not-allowed" : "bg-black hover:bg-gray-900"
            )}
          >
            {submitting ? "Submitting…" : requiredOk ? "Submit" : "Complete required"}
          </button>
        </div>
      </main>
    </div>
  );
}
